import MeetUpList from "../components/meetups/MeetUpList";
import { useState, useEffect } from "react";

function AllMeetUpsPage() {
  const [isLoading, setIsLoading] = useState(false);
  const [loadedMeetups, setLoadedMeetups] = useState([]);
  useEffect(() => {
    setIsLoading(true);
    fetch(
      "https://react-getting-started-6e72f-default-rtdb.firebaseio.com/meetups.json"
    )
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        const meetups = [];
        for (const key in data) {
          const meetup = {
            id: key,
            ...data[key],
          };
          meetups.push(meetup);
        }
        setIsLoading(false);
        setLoadedMeetups(meetups);
      });
  }, []);

  async function loadeMeetups(){
    fetch('https://react-getting-started-6e72f-default-rtdb.firebaseio.com/meetups.json')
    .then((response) => {
      return response.json();
    })
  }
  if (isLoading) {
    return (
      <section>
        <p>Carregando Meetups...</p>
      </section>
    );
  }

  return (
    <div>
      <h1>All Meet Ups</h1>
      <MeetUpList itemsList={loadedMeetups} />
    </div>
  );
}

export default AllMeetUpsPage;
